              <p className="text-sm font-medium text-gray-600">Active Classes</p>
              <p className="text-2xl font-bold text-gray-900">
                {realTimeAttendance?.totalActiveClasses || 0}
              </p>
            </div>
          </div>
        </div>

        <div className="card p-6">
