import React, { useState } from 'react';
import { studentAPI } from '../services/api';

const QRScanner = () => {
  const [qrString, setQrString] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!qrString.trim()) {
      setMessage('Please enter a QR code string');
      setMessageType('error');
      return;
    }

    setLoading(true);
    setMessage('');

    try {
      const response = await studentAPI.markAttendance({ qrString: qrString.trim() });
      
      setMessage('Attendance marked successfully!');
      setMessageType('success');
      setQrString('');
      
      // Clear success message after 3 seconds
      setTimeout(() => {
        setMessage('');
      }, 3000);
      
    } catch (error) {
      setMessage(error.response?.data?.message || 'Failed to mark attendance');
      setMessageType('error');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-md mx-auto">
      <div className="card p-6">
        <div className="text-center mb-6">
          <h2 className="text-2xl font-bold text-gray-900 mb-2">ğŸ“± Scan QR Code</h2>
          <p className="text-gray-600">Enter the QR code string to mark your attendance</p>
        </div>

        {message && (
          <div className={`mb-4 p-3 rounded-lg ${
            messageType === 'success' 
              ? 'bg-green-100 border border-green-400 text-green-700'
              : 'bg-red-100 border border-red-400 text-red-700'
          }`}>
            {message}
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label htmlFor="qrString" className="block text-sm font-medium text-gray-700 mb-2">
              QR Code String
            </label>
            <input
              type="text"
              id="qrString"
              value={qrString}
              onChange={(e) => setQrString(e.target.value)}
              className="input-field"
              placeholder="Enter QR code string here..."
              disabled={loading}
            />
          </div>

          <button
            type="submit"
            disabled={loading || !qrString.trim()}
            className="w-full btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {loading ? 'Marking Attendance...' : 'Mark Attendance'}
          </button>
        </form>

        <div className="mt-6 p-4 bg-blue-50 rounded-lg">
          <h3 className="text-sm font-medium text-blue-800 mb-2">How to use:</h3>
          <ul className="text-xs text-blue-700 space-y-1">
            <li>â€¢ Ask your teacher to generate a QR code</li>
            <li>â€¢ Copy the QR code string from the teacher's screen</li>
            <li>â€¢ Paste it in the field above and click "Mark Attendance"</li>
            <li>â€¢ QR codes expire after 30 seconds</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default QRScanner;
