import React, { useEffect, useState } from 'react';
import { adminAPI } from '../services/api';

const AdminReportsPage = () => {
  const [reports, setReports] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchReports = async () => {
      try {
        const response = await adminAPI.getReports();
        // Backend returns: { overall, students, subjects, dailyTrends }
        setReports(response.data.data || {});
      } catch (err) {
        setError('Failed to load reports');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchReports();
  }, []);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  if (error) {
    return <div className="text-red-600">{error}</div>;
  }

  const overall = reports?.overall || {};
  const students = Array.isArray(reports?.students) ? reports.students : [];
  const subjects = Array.isArray(reports?.subjects) ? reports.subjects : [];
  const dailyTrends = Array.isArray(reports?.dailyTrends) ? reports.dailyTrends : [];

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-3xl font-bold text-gray-900">Reports</h1>
        <p className="text-gray-600">Attendance analytics and summaries.</p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <div className="card p-6">
          <p className="text-sm text-gray-600">Total Students</p>
          <p className="text-2xl font-bold text-gray-900">{overall.total_students ?? '-'}</p>
        </div>
        <div className="card p-6">
          <p className="text-sm text-gray-600">Total Classes</p>
          <p className="text-2xl font-bold text-gray-900">{overall.total_classes ?? '-'}</p>
        </div>
        <div className="card p-6">
          <p className="text-sm text-gray-600">Total Attendance Records</p>
          <p className="text-2xl font-bold text-gray-900">{overall.total_attendance_records ?? '-'}</p>
        </div>
        <div className="card p-6">
          <p className="text-sm text-gray-600">Average Attendance %</p>
          <p className="text-2xl font-bold text-gray-900">{overall.average_attendance_percentage != null ? `${Number(overall.average_attendance_percentage).toFixed(2)}%` : '-'}</p>
        </div>
      </div>

      {students.length > 0 && (
        <div className="card p-6">
          <h2 className="text-xl font-semibold text-gray-900 mb-4">Students by Attendance</h2>
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="table-header">Name</th>
                  <th className="table-header">Email</th>
                  <th className="table-header">Attended</th>
                  <th className="table-header">Total Classes</th>
                  <th className="table-header">Attendance %</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {students.map((s) => (
                  <tr key={s.id}>
                    <td className="table-cell font-medium">{s.name}</td>
                    <td className="table-cell">{s.email}</td>
                    <td className="table-cell">{s.attended_classes}</td>
                    <td className="table-cell">{s.total_classes}</td>
                    <td className="table-cell">{s.attendance_percentage != null ? `${Number(s.attendance_percentage).toFixed(2)}%` : '-'}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {subjects.length > 0 && (
        <div className="card p-6">
          <h2 className="text-xl font-semibold text-gray-900 mb-4">Attendance by Subject</h2>
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="table-header">Subject</th>
                  <th className="table-header">Classes</th>
                  <th className="table-header">Unique Students</th>
                  <th className="table-header">Attendance Records</th>
                  <th className="table-header">Attendance %</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {subjects.map((subj, idx) => (
                  <tr key={idx}>
                    <td className="table-cell font-medium">{subj.subject}</td>
                    <td className="table-cell">{subj.total_classes}</td>
                    <td className="table-cell">{subj.unique_students_attended}</td>
                    <td className="table-cell">{subj.total_attendance_records}</td>
                    <td className="table-cell">{subj.attendance_percentage != null ? `${Number(subj.attendance_percentage).toFixed(2)}%` : '-'}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {dailyTrends.length > 0 && (
        <div className="card p-6">
          <h2 className="text-xl font-semibold text-gray-900 mb-4">Daily Attendance (Last 30 Days)</h2>
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="table-header">Date</th>
                  <th className="table-header">Classes Held</th>
                  <th className="table-header">Attendance Records</th>
                  <th className="table-header">Attendance %</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {dailyTrends.map((d, idx) => (
                  <tr key={idx}>
                    <td className="table-cell font-medium">{new Date(d.date).toLocaleDateString()}</td>
                    <td className="table-cell">{d.classes_held}</td>
                    <td className="table-cell">{d.attendance_records}</td>
                    <td className="table-cell">{d.daily_attendance_percentage != null ? `${Number(d.daily_attendance_percentage).toFixed(2)}%` : '-'}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminReportsPage;
